const Chatbox = () => {
    return (  
        <div className="chatbox">
            {/* Chat Area */}
            <div 
      className="chatbox" 
      style={{
        display: 'flex', 
        flexDirection: 'column', 
        justifyContent: 'space-between', 
        height: '100vh', // Makes the Chatbox fill the viewport height
        width: '100%', // Makes it fill the entire width
        boxSizing: 'border-box', // Ensures padding doesn't increase element dimensions
        padding: '20px' // Adds consistent padding
      }}
    >

            {/* Input and Button Section */}
            <div style={{marginTop: '20px', display: 'flex', flexDirection: 'column', gap: '10px' }}>
                <input type="text" placeholder="How can MediCompanion help?" style={{ padding: '10px', fontSize: '16px', border: '1px solid #ddd', borderRadius: '5px', flex: 1 }} />
                <div style={{ display: 'flex', gap: '10px' }}>
                    <button style={{ padding: '10px 20px', fontSize: '14px', border: 'none', borderRadius: '5px', backgroundColor: '#ddd', cursor: 'pointer' }}>Prescriptions...</button>
                    <button style={{ padding: '10px 20px', fontSize: '14px', border: 'none', borderRadius: '5px', backgroundColor: '#ddd', cursor: 'pointer' }}>Vaccines...</button>
                    <button style={{ padding: '10px 20px', fontSize: '14px', border: 'none', borderRadius: '5px', backgroundColor: '#ddd', cursor: 'pointer' }}>I feel sick...</button>
                </div>
            </div>

            </div>


        </div>

    );
}

export default Chatbox;
